<?const VITE_HOST='http://localhost:1111';function add_vite() {wp_enqueue_script('vite-client',VITE_HOST.'/@vite/client',array(),null,true);wp_enqueue_script('vite-app',VITE_HOST.'/components/wordpress/fls-theme/assets/app.js',array(),null,true);};add_action('wp_enqueue_scripts', 'add_vite');function add_type_module_attribute($tag, $handle) {if ( 'vite-client' === $handle || 'vite-app' === $handle) {return str_replace('<script', '<script type="module"', $tag);};return $tag;};add_filter('script_loader_tag', 'add_type_module_attribute', 10, 2);?>